import { Box, Checkbox, Radio, withStyles } from "@material-ui/core";
import { useContext } from "react";
import { QuizTestStateContext } from "../../../../Questions";
import { QuestionContext } from "../../../SingleQuestion";
import useOption from "./useOption";

interface IProps {
  option: any;
}

export default function Option({ option }: IProps) {
  const { answers, setAnswers } = useContext(QuizTestStateContext);
  const { question } = useContext(QuestionContext);

  const { isChecked, handleClick } = useOption(
    option?.id,
    question,
    answers,
    setAnswers
  );

  return (
    <Container onClick={handleClick}>
      {question?.answer_type === "multiple_choice" && (
        <Radio checked={isChecked} />
      )}
      {question?.answer_type === "checkboxes" && (
        <Checkbox checked={isChecked} />
      )}
      {option?.title}
    </Container>
  );
}

const Container = withStyles({
  root: {
    borderTop: "1px solid gray",
    padding: "0.1rem 0rem",
    display: "flex",
    alignItems: "center",
    cursor: "pointer",
  },
})(Box);

const TitleAndImageContainer = withStyles({
  root: {
    width: "100%",
    display: "flex",
    flexDirection: ""
  },
})(Box);
