import { makeStyles } from "@material-ui/core";
import React from "react";

interface IProps {
  isShown: boolean;
  setIsShown: Function;
}

const useStyles = makeStyles({
  container: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "black",
    zIndex: 100,
    overFlowY: "scroll",
    overflowX: "hidden",
    opacity: 0.6,
  },
});

const SearchModal: React.FC<IProps> = ({ isShown, setIsShown }) => {
  if (!isShown) return <></>;
  return (
    <div
      className={classes.container}
      onClick={() => {
          setIsShown(true)
      }}
    ></div>
  );
};

export default SearchModal;
