import { useEffect, useState } from "react";

export default function useBottomBar(testStatus: String) {
  const [buttonText, setButtonText] = useState<"start" | "retake">("start");

  useEffect(() => {
    if (testStatus === "retake") {
      setButtonText("retake");
    } else if (testStatus === "highest_attempt_count_reached") {

    } else {
      setButtonText("start");
    }
  }, [testStatus]);

  return { buttonText };
}
