import { Box, Typography, withStyles } from "@material-ui/core";
import { useContext } from "react";
import { QuizTestStateContext } from "../Questions";

interface IProps {
  questionCount: number;
}

export default function QuestionCountAndTimer({ questionCount }) {
  const { currentQuestionIndex } = useContext(QuizTestStateContext);
  return (
    <Container>
      <QuestionCount>{`${
        currentQuestionIndex + 1
      } / ${questionCount}`}</QuestionCount>
      <TimeRemaining>0:0</TimeRemaining>
    </Container>
  );
}

const Container = withStyles({
  root: {
    float: "left",
    display: "flex",
    alignItems: "center",
  },
})(Box);

const QuestionCount = withStyles({
  root: {
    marginRight: "0.5rem",
  },
})(Typography);

const TimeRemaining = withStyles({
  root: {},
})(Typography);
