import { Box, Typography, withStyles } from "@material-ui/core";
import { useContext } from "react";
import { QuizTestStateContext } from "../../Questions";

export default function QuestionCountAndTimer(timeRemaing: string) {
  const { questionCount, currentQuestionIndex, timeRemaining } = useContext(
    QuizTestStateContext
  );
  return (
    <Container>
      <QuestionCount variant="subtitle2">{`Q.${
        currentQuestionIndex + 1
      } / ${questionCount}`}</QuestionCount>
      <TimeRemaining variant="subtitle2">{timeRemaining}</TimeRemaining>
    </Container>
  );
}

const Container = withStyles({
  root: {
    float: "left",
    display: "flex",
    alignItems: "center",
  },
})(Box);

const QuestionCount = withStyles({
  root: {
    marginRight: "1rem",
    fontWeight: 600,
  },
})(Typography);

const TimeRemaining = withStyles({
  root: {
    fontWeight: 600,
  },
})(Typography);
