import {
  Avatar,
  Box,
  CircularProgress,
  Divider,
  Typography,
  withStyles,
} from "@material-ui/core";
import { backend_url } from "../../../../../../url";
import BottomBar from "./BottomBar/BottomBar";
import useQuizInfo from "./useQuizInfo";

import { QueryBuilder, QuestionAnswerOutlined } from "@material-ui/icons";
import getFormattedTime from "../../../../../../UtilFunctions/getFormattedTime";

interface IProps {
  quizId: number;
}

export default function QuizInfo({ quizId }: IProps) {
  const { testStatus, quizInfo, userInfo, isLoading } = useQuizInfo(quizId);


  return (
    <Container>
      {isLoading && (
        <LoadingContainer>
          <CircularProgress color="primary" />
        </LoadingContainer>
      )}
      <BottomBar testStatus={testStatus} />
    </Container>
  );
}

const Title = withStyles({
  root: {
    margin: "1rem 0 0 0",
    textTransform: "capitalize",
    fontWeight: 600,
  },
})(Typography);

const Description = withStyles({
  root: {
    margin: "0.2rem 0",
    textTransform: "capitalize",
  },
})(Typography);

const TestTakerCount = withStyles({
  root: {
    margin: "0.2rem 0",
    color: "gray",
  },
})(Typography);

const Container = withStyles({
  root: {
    padding: "1rem",
    display: "flex",
    flexDirection: "column",
  },
})(Box);

const LoadingContainer = withStyles({
  root: {
    width: "100%",
    display: "flex",
    justifyContent: "center",
  },
})(Box);

const ImageContainer = withStyles({
  root: {},
})(Box);

const QuizDetailContainer = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    margin: "1rem 0",
  },
})(Box);

const QuizDetail = withStyles({
  root: {
    display: "flex",
    alignItems: "center",
  },
})(Box);

const QuizDetailText = withStyles({
  root: {
    margin: "0 0 0 0.5rem",
  },
})(Typography);
