import axios from "axios";

import useLocalState from "../custom-hooks/useLocalState";

export default async function post(
  url,
  data,
  callBack,
  errorCallBack,
  isMultipart = false
) {
  const user = getUser();

  const config = {
    method: "post",
    url,
    headers: {
      Accept: "application/json",
      Authorization: `Bearer ${user.token}`,
    },
    data,
  };

  if (isMultipart) {
    config = {
      ...config,
      "Content-Type": "multipart/form-data",
    };
  }

  axios(config)
    .then((response) => {
      callBack(response);
    })
    .catch((error) => {
      errorCallBack(error);
    });
}

function getUser() {
  const key = "user";
  const defaultValue = "";

  if (process.browser) {
    const storedValue = JSON.parse(localStorage.getItem(key));
    if (!storedValue) {
      return defaultValue;
    }
    return storedValue === null ? defaultValue : storedValue;
  } else {
    return defaultValue;
  }
}
