import { Box, Slide, withStyles } from "@material-ui/core";
import { useContext, useEffect, useState } from "react";
import IQuestion from "../../../../Models/IQuestion";
import { QuizTestStateContext } from "../Questions";

import Question from "./Question/"

interface IProps {
  index: number;
  question: IQuestion;
}

export default function SingleQuestion({ question, index }: IProps) {
  const { currentQuestionIndex } = useContext(QuizTestStateContext);
  const [isVisible, setIsVisible] = useState(index == currentQuestionIndex);
  const [direction, setDirection] = useState<"left" | "right">("right");

  useEffect(() => {
    // console.log(
    //   `${question?.id} is ${
    //     isVisible ? "visible" : "not visible"
    //   } --- current: ${currentQuestionId}`
    // );
    if (currentQuestionIndex > index) {
      setDirection("right");
    } else if (currentQuestionIndex < index) {
      setDirection("left");
    }

    setIsVisible(index == currentQuestionIndex);
  }, [currentQuestionIndex]);

  return (
    <Slide
      in={isVisible}
      direction={direction}
      timeout={{ enter: 500, exit: 100 }}
      mountOnEnter
      unmountOnExit
    >
      {isVisible ? (
        <Container>

        </Container>
      ) : (
        <div></div>
      )}
    </Slide>
  );
}

const Container = withStyles({
  root: {
    display: "flex",
    flexDirection: "column",
    padding: "0.5rem 1rem",
  },
})(Box);
