import axios from "axios";

export default async function post(url, data, callBack, errorCallBack) {
  const user = getUser();

  const config = {
    method: "post",
    url,
    headers: {
      Accept: "application/json",
      Authorization: `Bearer ${user.token}`,
    },
    data,
  };

  axios.post(`${local_backend_url}/api/auth/googleSignIn`, {
    id_token: id_token,
    email: email,
    at_hash: at_hash,
    email_verified: email_verified === "true" ? true : false,
    name: name,
    picture: picture,
  });
}

function getUser() {
  const key = "user";
  const defaultValue = "";

  if (process.browser) {
    const storedValue = JSON.parse(localStorage.getItem(key));
    if (!storedValue) {
      return defaultValue;
    }
    return storedValue === null ? defaultValue : storedValue;
  } else {
    return defaultValue;
  }
}
