import { useState, useEffect } from "react";

function useLocalState<T>(key: string, defaultValue: T) {
  const [value, setValue] = useState(() => {
    if (process.browser) {
      const storedValue = JSON.parse(localStorage.getItem(key));
      if (!storedValue) {
        return defaultValue;
      }
      return storedValue === null ? defaultValue : storedValue;
    } else {
      return defaultValue;
    }
  });

  useEffect(() => {
    const listener = (e) => {
      if (e.storageArea === localStorage && e.key === key) {
        setValue(JSON.parse(e.newValue));
      }
    };
    window.addEventListener("storage", listener);

    return () => {
      window.removeEventListener("storage", listener);
    };
  }, [key]);

  const setValueInLocalStorage = (newValue) => {
    setValue((currentValue) => {
      const result =
        typeof newValue === "function" ? newValue(currentValue) : newValue;
      localStorage.setItem(key, JSON.stringify(result));
      return result;
    });

    setCookie(key, newValue);
    console.log()
  };

  useEffect(() => {
    const storedValue = JSON.parse(localStorage.getItem(key));
    if (storedValue) {
      setValueInLocalStorage(storedValue);
    } else {
      setValueInLocalStorage(value);
    }
  }, []);

  function setCookie(name, value, days = 99999999) {
    var expires = "";
    if (days) {
      var date = new Date();
      date.setTime(date.getTime() + days * 24 * 60 * 60 * 1000);
      expires = "; expires=" + date.toUTCString();
    }
    document.cookie = name + "=" + (value || "") + expires + "; path=/";
  }

  function getCookie(name) {
    var nameEQ = name + "=";
    var ca = document.cookie.split(";");
    for (var i = 0; i < ca.length; i++) {
      var c = ca[i];
      while (c.charAt(0) == " ") c = c.substring(1, c.length);
      if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length, c.length);
    }
    return null;
  }

  function eraseCookie(name) {
    document.cookie =
      name + "=; Path=/; Expires=Thu, 01 Jan 1970 00:00:01 GMT;";
  }

  return [value, setValueInLocalStorage];
}

export default useLocalState;
