import { Box, Slide, withStyles } from "@material-ui/core";
import { useEffect, useState } from "react";
import IQuestion from "../../../../Models/IQuestion";

interface IProps {
  question: IQuestion;
  currentQuestionId: number;
}

export default function Question({ question, currentQuestionId }: IProps) {
  const [isVisible, setIsVisible] = useState(question?.id == currentQuestionId);

  useEffect(() => {
    console.log(
      `${question?.id} is ${
        isVisible ? "visible" : "not visible"
      } --- current: ${currentQuestionId}`
    );
    setIsVisible(question?.id == currentQuestionId)
  }, [currentQuestionId]);

  return (
    <Slide in={isVisible} direction="left" mountOnEnter unmountOnExit>
      <Container>{question?.question}</Container>
    </Slide>
  );
}

const Container = withStyles({
  root: {
    padding: "0.5rem 1rem",
  },
})(Box);
