import { Box, Typography, withStyles } from "@material-ui/core";
import IQuestion from "../../../Models/IQuestion";
import Question from "./Question/Question";
import QuestionBottomBar from "./QuestionBottomBar/QuestionBottomBar";
import useTestState from "./useTestState";

interface IProps {
  quizTitle: String;
  questions: IQuestion[];
  isAssessment: boolean;
  quizId: number;
}

export default function Questions({
  quizTitle,
  questions,
  isAssessment,
  quizId,
}: IProps) {
  const { currentQuestionId, goToNextQuestion } = useTestState(
    quizId,
    questions
  );

  return (
    <Container>
      <Header>
        <Typography variant="h6" color="inherit">
          {`${quizTitle} ${isAssessment ? "assessment" : "quiz"}`}
        </Typography>
      </Header>
      <Question question={currentQuestionId} />
      <QuestionBottomBar goToNextQuestion={goToNextQuestion} />
    </Container>
  );
}

const Container = withStyles({
  root: {
    backgroundColor: "lightblue",
    width: "min(97%, 600px)",
    borderRadius: 5,
    overflow: "hidden",
    boxShadow: "0px 0px 8px 0px #2b579a",
    display: "flex",
    flexDirection: "column",
  },
})(Box);

const Header = withStyles({
  root: {
    width: "100%",
    textAlign: "center",
    color: "white",
    padding: "0.5rem 0",
    background:
      "transparent linear-gradient(180deg, #003EAA 0%, #002461E0 100%) 0% 0% no-repeat padding-box",
    textTransform: "capitalize",
  },
})(Box);
